    d.z_score = function(data, options) {
        var grey = {
            "50": "#FAFAFA",
            "100": "#F5F5F5",
            "200": "#EEEEEE",
            "300": "#E0E0E0",
            "400": "#BDBDBD",
            "500": "#9E9E9E",
            "600": "#757575",
            "700": "#616161",
            "800": "#424242",
            "900": "#212121"
        };

        var default_data = {
            "zscore": 2.2,
            "zscore_color": "#3F51B5",
            "clinicsample_start": -1.4,
            "clinicsample_end": 1.5
        };

        var default_options = {
            "width": 270,
            "zscore_min": -3,
            "zscore_max": 3,
            "clinicsample_color": grey["300"]
        };

        data = data === undefined ? default_data : data;
        options = options === undefined ? default_options : options;

        // Always grey
        options.clinicsample_color = grey["300"];


        // Calculate

        options.count_steps = 0;
        if (options.zscore_min <= 0) {
            options.count_steps = Math.abs(options.zscore_min) + Math.abs(options.zscore_max);
        } else {
            options.count_steps = Math.abs(options.zscore_max) - Math.abs(options.zscore_min);
        };

        options.step_1 = options.width / options.count_steps;

        var get_x = function(value) {
            return Math.abs(options.zscore_min - value) * options.step_1
        };


        var canvas = [];
        var obj_to_push = {};

        // Basic Chart
        obj_to_push = {
            "type": "line",
            "x1": get_x(data.clinicsample_start),
            "y1": 15,
            "x2": get_x(data.clinicsample_end),
            "y2": 15,
            "lineWidth": 30,
            "lineColor": options.clinicsample_color
        };

        canvas.push(obj_to_push);

        obj_to_push = {
            "type": "rect",
            "x": 0,
            "y": 0,
            "w": options.width,
            "h": 30,
            "lineColor": grey["400"]
        };
        canvas.push(obj_to_push);


        for (var i = 0; i < options.count_steps + 1; i++) {
            var my_x = get_x(options.zscore_min + i);
            if (options.zscore_min + i !== 0) {
                obj_to_push = {
                    "type": "line",
                    "x1": my_x,
                    "y1": 0,
                    "x2": my_x,
                    "y2": 30,
                    "lineWidth": 1,
                    "lineColor": grey["200"]
                };
                canvas.push(obj_to_push);

                obj_to_push = {
                    "type": "line",
                    "x1": my_x,
                    "y1": 25,
                    "x2": my_x,
                    "y2": 35,
                    "lineWidth": 1,
                    "lineColor": grey["500"]
                };
                canvas.push(obj_to_push);
            };
        };

        obj_to_push = {
            "type": "line",
            "x1": get_x(data.clinicsample_start),
            "y1": 0,
            "x2": get_x(data.clinicsample_start),
            "y2": 30,
            "lineWidth": 1,
            "lineColor": grey["600"]
        };
        canvas.push(obj_to_push);

        obj_to_push = {
            "type": "line",
            "x1": get_x(data.clinicsample_end),
            "y1": 0,
            "x2": get_x(data.clinicsample_end),
            "y2": 30,
            "lineWidth": 1,
            "lineColor": grey["600"]
        };
        canvas.push(obj_to_push);

        obj_to_push = {
            "type": "line",
            "x1": get_x(data.zscore),
            "y1": 15,
            "x2": get_x(0),
            "y2": 15,
            "lineWidth": 15,
            "lineColor": data.zscore_color
        };
        canvas.push(obj_to_push);

        obj_to_push = {
            "type": "line",
            "x1": get_x(0),
            "y1": 0,
            "x2": get_x(0),
            "y2": 35,
            "lineWidth": 2,
            "lineColor": grey["900"]
        };
        canvas.push(obj_to_push);

        // console.log(JSON.stringify(canvas, null, 2));

        return canvas;
    };

    d.z_score_zahlen = function(zscore_min, zscore_max, width) {

        // Zahlen -3 | 0 | +3
        var count_steps = 0;
        if (zscore_min <= 0) {
            count_steps = Math.abs(zscore_min) + Math.abs(zscore_max);
        } else {
            count_steps = Math.abs(zscore_max) - Math.abs(zscore_min);
        };

        var zahlen_to_push = {
            "columns": [],
            "width": width,
            "columnGap": 0,
            "fontSize": 8,
            "color": "#757575"
        };

        for (var i = 0; i < count_steps; i++) {
            var value = zscore_min + i;
            var value_1 = zscore_min + i + 1;

            var my_bold = false;
            if (value === 0) {
                my_bold = true;
            };

            var obj_to_push = {
                "width": "*",
                "text": value.toString(),
                "bold": my_bold,
                "alignment": "left"
            };

            if (i === count_steps - 1) {
                obj_to_push = {
                    "width": "*",
                    "columns": [
                        { "text": value.toString(), "alignment": "left" },
                        { "text": value_1.toString(), "alignment": "right" }
                    ]
                };
            };

            zahlen_to_push.columns.push(obj_to_push);
        };

        return zahlen_to_push;
    };

    d.stanine = function(stanine, mz, width) {

        stanine = stanine === undefined ? null : stanine;
        mz = mz === undefined ? null : mz;
        width = width === undefined ? 240 : parseInt(width);



        var step_1 = width / 9;


        var canvas = [];


        var return_stanine = {
            "width": width,
            "alignment": "center",
            "canvas": [{
                "type": "rect",
                "x": 0,
                "y": 0,
                "w": width,
                "h": 30,
                "lineColor": "#E0E0E0"
            }, {
                "type": "rect",
                "x": step_1,
                "y": 0,
                "w": step_1,
                "h": 30,
                "lineColor": "#E0E0E0"
            }, {
                "type": "rect",
                "x": step_1 * 3,
                "y": 0,
                "w": step_1,
                "h": 30,
                "lineColor": "#E0E0E0"
            }, {
                "type": "rect",
                "x": step_1 * 5,
                "y": 0,
                "w": step_1,
                "h": 30,
                "lineColor": "#E0E0E0"
            }, {
                "type": "rect",
                "x": step_1 * 7,
                "y": 0,
                "w": step_1,
                "h": 30,
                "lineColor": "#E0E0E0"
            }]
        };


        var eintritt = {
            "type": "ellipse",
            "x": step_1 * (stanine - 1) + step_1 / 2,
            "y": 15,
            "color": "#3F51B5",
            "r1": 12,
            "r2": 12
        };

        var austritt = {
            "type": "rect",
            "x": step_1 * (stanine - 1),
            "y": 0,
            "w": step_1,
            "h": 30,
            "color": "#3F51B5",
            "lineColor": "#E0E0E0"
        };

        var anderer_1 = {
            "type": "rect",
            "x": step_1 * (stanine - 1),
            "y": 0,
            "w": step_1,
            "h": 30,
            "color": "#3F51B5",
            "lineColor": "#E0E0E0"
        };

        var anderer_2 = {
            "type": "ellipse",
            "x": step_1 * (stanine - 1) + step_1 / 2,
            "y": 15,
            "color": "#FFFFFF",
            "r1": 13,
            "r2": 13
        };

        if (mz === "Eintritt") {
            return_stanine.canvas.push(eintritt);
        };

        if (mz === "Austritt") {
            return_stanine.canvas.push(austritt);
        };

        if ((mz !== "Eintritt") && (mz !== "Austritt")) {
            return_stanine.canvas.push(anderer_1);
            return_stanine.canvas.push(anderer_2);
        };


        return return_stanine;
    };

